import React, { useState } from "react";
import "../Modal.css";
import close from '../assets/close.png';
import { dataDecorations } from "../data/dataDecorations";
import circle from '../assets/circle.png'; 
import arrowLeft from '../assets/arrowLeft.png';
import arrowRight from '../assets/arrowRight.png';
import Slider from "./Slider";

function DecorModal({id,modal,toggleModal}) {

  return (
    <>

      {modal && (
        <div className="modal">
          <div onClick={toggleModal} className="overlay"></div>
          <div className="modal-content2">
            <h2>{dataDecorations[id].title}</h2>
            <div>
            <Slider id={id}/>
            </div>
            <div>
            <div className="wrapper6">
            <h2 className="titleProgram">Программа и активности</h2>
            <p className="text wrapText">{dataAnimators[id].description}</p>
            </div>
            </div>
          </div>

          <img className="close-modal close" onClick={toggleModal} src={close} alt="close"/>
          </div>
      )}

    </>
  );
}

export default DecorModal;
