import SliderItems from "./SliderItems";
import { dataDecorations } from '../data/dataDecorations';
import circle from '../assets/circle.png'; 

function DecorItem({id}) {
    return (
        <div className="modalPortal">
            <h2>{dataDecorations[id].title}</h2>
            div>
            <div className="sliderDecorCont">
            <SliderItems id={id}/>
            </div>
            <div>
                <h3>{dataDecorations[id].header}</h3>
                <p>{dataDecorations[id].description}</p>
                <h3>{dataDecorations[id].header2}</h3>
                {dataDecorations[id].program.map((item,index) => {
                    return (
                        <div key={index}>
                        <img src={circle} alt="circle"/><li>{item}</li>
                        </div>
                    )
                })}
            </div>
        </div>
    )
}

export default DecorItem;
