import React, { useState } from "react";
import "../Modal.css";
import close from '../assets/close.png';
import { dataDecorations } from "../data/dataDecorations";
import arrowLeft from '../assets/arrowLeft.png';
import arrowRight from '../assets/arrowRight.png';

export default function Modal({id}) {
  const [modal, setModal] = useState(false);

  const toggleModal = () => {
    setModal(!modal);
  };

  if(modal) {
    document.body.classList.add('active-modal')
  } else {
    document.body.classList.remove('active-modal')
  }

  const [slides, setSlides] = useState(0);
    const slider = dataDecorations[id].slider;

    const [show, setShow] = useState(false);

    const previousSlide = () => {
        setSlides((slides => {
            slides--;
            if(slides<0) {
                return dataDecorations[id].slider.length-1
            }
            return slides
        }))
    }

    const nextSlide = () => {
        setSlides((slides => {
            slides++;
            if(slides > dataDecorations[id].slider.length-1) {
                return slides = 0
            }
            return slides
        }))
    }

  return (
    <>


      {modal && (
        <div className="modal">
          <div onClick={toggleModal} className="overlay"></div>
          <div className="modal-content2">
            <div className="modalBox1">
            <div className="block2">
                <img onClick={previousSlide} src={arrowLeft} width="40px" alt="arrow" className="arrow"/>
                <img src={dataDecorations[id].slider[0]} alt="options" className="imageOptions"/>
                <img onClick={nextSlide} src={arrowRight} width="60px" alt="arrow" className="arrow"/>
            </div>
            <div className="priceWrapper">
                <span className="animatorPrice">от {dataDecorations[id].price} ₽ / час</span>
                <button className="btnCTA">Получить расчет</button>
            </div>

            </div>
            <div className="modalBox2">

            </div>
         </div>

          <img className="close-modal close" onClick={toggleModal} src={close} alt="close"/>
          </div>
      )}

    </>
  );
}
