import InputMask from "react-input-mask";
import React, {useState} from "react";
import { useData } from "./DataContext";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";

function FormFeedback() {
    const { setValues, data } = useData();

    const { register, handleSubmit, errors } = useForm({
      defaultValues: { firstName: data.firstName, lastName: data.lastName },
      mode: "onBlur",
      resolver: yupResolver(schema),
    });

    return (
        <div className="modalForm">
            <div className="modal__title">
                Оставьте заявку 
            </div>
            <div class="modal__description">
                Наш эксперт развлечений ответит на&nbsp;все ваши вопросы. 
            </div>
            <form className="gray-form vertical-form">
                <label className="input">
                    <span className="input__label menu-18 gray-form" >Имя</span>
                    <input type="text" name="user_name" placeholder="Как вас зовут?" data-label="Имя пользователя" className="input-text gray-form"/>
                </label>
                <label className="input">
                    <span className="input__label menu-18 gray-form">Телефон*</span>
                    <InputMask 
                    type="tel" 
                    name="user_tel" 
                    placeholder="Введите телефон*" 
                    data-label="Телефон"  
                    data-req="true" 
                    maxlength="18" 
                    className="input-text" 
                    mask="+7 999 999 99 99">
    </InputMask>
                </label>
                <button className="btnCTA center" type="submit">Отправить</button>
            </form>
        </div>
    )
}

export default FormFeedback;
