import { useState } from "react";
import React from "react";
import { dataDecorations } from '../data/dataDecorations'
import DecorModal from "./DecorModal";

const BUTTON_WRAPPER_STYLES = {
    position: 'relative',
    zIndex: 1
  }

  const OTHER_CONTENT_STYLES = {
    position: 'relative',
    zIndex: 2,
    backgroundColor: 'red',
    padding: '10px'
  }

function Decorations() {
    const [showComponent, setShowComponent] = useState(true);
    const [itemId, setItemId] = useState(0);
    const [isOpen, setIsOpen] = useState(false)

    const toggleModal = () => {
      setModal(!modal);

    };

    if(modal) {
      document.body.classList.add('active-modal');
    } else {
      document.body.classList.remove('active-modal')
    }


    const viewItem = (id,e) => {
        e.preventDefault();
        setShowComponent(!showComponent);
        setItemId(id);
        setTimeout(toggleModal,200)
    }

    return (
        <div>
            {
             showComponent 
             ? 
     <div>
        <div className="contBanner4">
            <div className="wrapper5">
            <h1>Оформление детских праздников шарами и декорациями</h1>
            <p>Иногда не достает всего пары деталей, чтобы погружение в атмосферу волшебства было наиболее полным. Наша команда готова помочь и приложить все усилия, чтобы граница между сказочным и реальным миром почти перестала существовать.</p>
            <button className="btnCTA">Оставить заявку</button> 
            </div>
        </div>
        <div className="wrapper6">

            <div className="wrapper8">

            {dataDecorations.map((item => {
                const {id,title,image} = item;
                if(id===0) { 
                    return (
                        <div  onClick={() => setIsOpen(true)} key={id} className="photoZone" style={{backgroundImage:`url(${image})`}}>
                            <p className="decorHeader1">{title}</p>
                        </div>
                        ) } 
                if (id>=3) {
                    return (
                        <div onClick={(e) => viewItem(id,e)} key={id} className="cell bottomCell" style={{backgroundImage:`url(${image})`}}>
                            <p className="decorHeader2">{title}</p>
                        </div>
                        ) 
                }
                    else { 
                        return (
                            <div onClick={(e) => viewItem(id,e)} key={id} className="cell " style={{backgroundImage:`url(${image})`}}>
                                <p className="decorHeader2">{title}</p>
                            </div>
                         ) }


            }))
            }
            </div>
        </div>
        </div>

 :
     <DecorModal  id={itemId} modal={modal} toggleModal={toggleModal}/>
      }

        </div>

    )
}


export default Decorations;
