import React from "react";

function ModalMap ({ isVisible = false, title, content, footer, onClose })  {
    const keydownHandler = ({ key }) => {
      switch (key) {
        case 'Escape':
          onClose();
          break;
        default:
      }
    };

    React.useEffect(() => {
      document.addEventListener('keydown', keydownHandler);
      return () => document.removeEventListener('keydown', keydownHandler);
    });

    return !isVisible ? null : (
      <div className="modal" onClick={onClose}>
        <div className="modal-dialog" onClick={e => e.stopPropagation()}>
          <div className="modal-header">
            <h3 className="modal-title">{title}</h3>
            <span className="modal-close" onClick={onClose}>
              &times;
            </span>
          </div>
          <div className="modal-body">
            <div className="modal-content">
            <div style="position:relative;overflow:hidden;"><a href="https://yandex.ru/maps/213/moscow/?utm_medium=mapframe&utm_source=maps">Москва</a><a href="https://yandex.ru/maps/213/moscow/house/tuchkovskaya_ulitsa_11k2/Z04YcwdpSEMDQFtvfXt1d35hZw==/?indoorLevel=1&ll=37.509163%2C55.746223&utm_medium=mapframe&utm_source=maps&z=17.17" style="color:#eee;font-size:12px;position:absolute;top:14px;">Тучковская улица, 11к2 — Яндекс Карты</a><iframe src="https://yandex.ru/map-widget/v1/?indoorLevel=1&ll=37.509163%2C55.746223&mode=search&ol=geo&ouri=ymapsbm1%3A%2F%2Fgeo%3Fdata%3DCgg1NjcxMDA2MxJC0KDQvtGB0YHQuNGPLCDQnNC-0YHQutCy0LAsINCi0YPRh9C60L7QstGB0LrQsNGPINGD0LvQuNGG0LAsIDEx0LoyIgoNYwkWQhUh_F5C&z=17.17" width="560" height="400" frameborder="1" allowfullscreen="true" style="position:relative;"></iframe></div></div>
          </div>
          {footer && <div className="modal-footer">{footer}</div>}
        </div>
      </div>
    );
  };

  export default ModalMap;
