import { dataSlides } from "./data/dataSlides";
import arrowLeft from './assets/arrowLeft.png';
import arrowRight from './assets/arrowRight.png';
import { useState } from "react";

function Slider()  {
    const [slides, setSlides] = useState(0);
    const {id,title,description,image} = dataSlides[slides];

    return (
        <div className="wrapper">
            <div className="block1">
                <h1 className="headerSlide">Праздник в деталях</h1>
                <h2 className="titleSlide">{title}</h2>
                <p className="text">{description}</p>
            </div>
            <div className="block2">
                <img src={arrowLeft} width="40px" alt="arrow" className="arrow"/>
                <img src={image} alt="options" className="imageOptions"/>
                <img src={arrowRight} width="60px" alt="arrow"/>
            </div>
        </div>
    )
}

export default Slider;
