import { useState } from "react";
import { dataDecorations } from '../data/dataDecorations'

function Slider({id})  {
    const [slides, setSlides] = useState(0);
    const list = dataDecorations[id].slider;

    const {title,description,image} = list[slides];

    const [show, setShow] = useState(false);

    const previousSlide = () => {
        setSlides((slides => {
            slides--;
            if(slides<0) {
                return list.length-1
            }
            return slides
        }))
    }

    const nextSlide = () => {
        setSlides((slides => {
            slides++;
            if(slides > list.length-1) {
                return slides = 0
            }
            return slides
        }))
    }

    return (
        <div>
   <h2>slider</h2>
        </div>
    )
}

export default Slider;
