import InputMask from "react-input-mask";
import React, {useState} from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

const schema = yup.object().shape({
    firstName: yup
      .string()
      .matches(/^([^0-9]*)$/, "First name should not contain numbers")
      .required("First name is a required field"),
    lastName: yup
      .string()
      .matches(/^([^0-9]*)$/, "Last name should not contain numbers")
      .required("Last name is a required field"),
  });

function FormFeedback() {

    const { register, handleSubmit, errors } = useForm({
      defaultValues: { firstName: data.firstName, lastName: data.lastName },
      mode: "onBlur",
      resolver: yupResolver(schema),
    });

    const onSubmit = (data) => {
        setValues(data);
      };

    return (
        <div className="modalForm">
            <div className="modal__title">
                Оставьте заявку 
            </div>
            <div class="modal__description">
                Наш эксперт развлечений ответит на&nbsp;все ваши вопросы. 
            </div>
            <form className="gray-form vertical-form">
                <label className="input">
                    <span className="input__label menu-18 gray-form" >Имя</span>
                    {/* <input type="text" name="user_name" placeholder="Как вас зовут?" data-label="Имя пользователя" className="input-text gray-form"/> */}
                    <input
          {...register('parentName')}
          id="firstName"
          type="text"
          label="First Name"
          name="firstName"
          error={!!errors.firstName}
          helperText={errors?.firstName?.message}
        />
                </label>
                <label className="input">
                    <span className="input__label menu-18 gray-form">Телефон*</span>
                    <InputMask 
                    type="tel" 
                    name="user_tel" 
                    placeholder="Введите телефон*" 
                    data-label="Телефон"  
                    data-req="true" 
                    maxlength="18" 
                    className="input-text" 
                    mask="+7 999 999 99 99">
    </InputMask>
                </label>
                <button className="btnCTA center" type="submit">Отправить</button>
            </form>
        </div>
    )
}

export default FormFeedback;
