import { useState } from "react";
import { dataMasterClasses } from '../data/dataMasterClasses'
import MasterClassItems from "./MasterClassItems";
import ItemDescription from "./ItemDescription";

function MasterClasses() {
    const [showComponent, setShowComponent] = useState(true);
    const [itemId, setItemId] = useState(0);

    return (
       <div>
     {showComponent 
     ? 
     <div>
        <div className="contBanner5">
            <div className="wrapper5">
            <h1>Мастер-классы</h1>
            <p>Наши аниматоры могут сделать из детей настоящих практикующих волшебников. Для этого понадобится немного свободного времени, желания и терпения. Обещаем, что после праздника юные гости овладеют новым навыком и, может быть, даже не одним.</p>
            <button className="btnCTA">Оставить заявку</button> 
            </div>
        </div>
        <div className="wrapper6">

            <div className="wrapper7">
            {dataMasterClasses.map((item => {
                const {id,title,image1} = item;
                return(
                    <div key={id}>
                        <MasterClassItems  title={title} id={id} image={image1} showComponent={showComponent} setShowComponent={setShowComponent} setItemId={setItemId}/>
                </div>
                )
            })
        )}
        </div>
        </div>
        </div>

        :
        <ItemDescription id={itemId}/>}

        </div>
    )
}

export default MasterClasses;
